import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState({});

    const fetchMyOrder = async () => {
        console.log(localStorage.getItem('userEmail'));
        await fetch("http://localhost:5000/api/myOrderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json();
            setOrderData(response);
        });
    }

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row mb-3'>
                    {orderData !== {} ? orderData.orderData?.order_data.slice(0).reverse().map((order, index) => (
                        order.map((item, idx) => (
                            <React.Fragment key={idx}>
                                {item.Order_date ? (
                                    <div className='w-100 text-center mt-5'>
                                        {date = item.Order_date}
                                        <hr />
                                        
                                    </div>
                                ) : (
                                    <div className='col-12 col-md-6 col-lg-3 mb-3'>
                                        <div className="card mt-3" style={{ width: "16rem", maxHeight: "400px" }}>
                                            <img src={item.img} className="card-img-top" alt={item.name} style={{ height: "120px", objectFit: "fill" }} />
                                            <div className="card-body">
                                                <h5 className="card-title">{item.name}</h5>
                                                <div className='container w-100 p-0'>
                                                    <p className='mb-1'>Qty: {item.qty}</p>
                                                    <p className='mb-1'>Size: {item.size}</p>
                                                    <p className='mb-1'>Date: {date}</p>
                                                    <div className='d-inline fs-5'>
                                                        â‚¹{item.price}/-
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </React.Fragment>
                        ))
                    )) : ""}
                </div>
            </div>
            <Footer />
        </div>
    );
}
