    return(
        <div>
            <CarDispatchContext.Provider value={dispatch}>
                <CarDispatchContext.Provider value={state}>
                    {Children}
                </CarDispatchContext.Provider>   
            </CarDispatchContext.Provider>
        </div>
    )
}

export const useCart = () => useContext(CartStateContext);
