import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState({});

    const fetchMyOrder = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/auth/myOrderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: localStorage.getItem('userEmail')
                })
            });
            if (!response.ok) {
                throw new Error('Failed to fetch data');
            }
            const data = await response.json();
            setOrderData(data);
        } catch (error) {
            console.error('Error fetching order data:', error);
        }
    };

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row row-cols-1 row-cols-md-2 row-cols-lg-4 g-3'>
                    {orderData?.orderData?.order_data?.map((order, index) => (
                        order.map((item, idx) => (
                            <div key={idx} className='col'>
                                <div className="card h-100">
                                    <img src={item.img} className="card-img-top" alt={item.name} style={{ height: "200px", objectFit: "cover" }} />
                                    <div className="card-body">
                                        <h5 className="card-title">{item.name}</h5>
                                        <p className='mb-1'>Qty: {item.qty}</p>
                                        <p className='mb-1'>Size: {item.size}</p>
                                        {item.Order_date && (
                                            <p className='mb-1'>Date: {new Date(item.Order_date).toLocaleDateString()}</p>
                                        )}
                                        <div className='fs-5'>
                                            â‚¹{item.price}/-
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))
                    ))}
                </div>
            </div>
            <Footer />
        </div>
    );
}
