import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    const fetchMyOrder = async () => {
        console.log(localStorage.getItem('userEmail'));
        await fetch("http://localhost:5000/api/myOrderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json();
            setOrderData(response.orderData ? response.orderData.order_data : []);
        });
    }

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <div>
                <Navbar />
            </div>

            <div className='container'>
                <div className='row mb-3'>
                    {orderData.length ? orderData.map((order, index) => (
                        <div key={index} className="col-12">
                            {order.map((item, idx) => (
                                <div key={idx}>
                                    {item[0]?.Order_date ? (
                                        <div className='m-auto mt-5'>
                                            <hr />
                                            <h5>Order Date: {new Date(item[0].Order_date).toLocaleString()}</h5>
                                        </div>
                                    ) : (
                                        <div className='row'>
                                            {item.map((arrayData, i) => (
                                                <div className='col-12 col-md-6 col-lg-3' key={i}>
                                                    <div className="card mt-3" style={{ width: "100%", maxHeight: "none" }}>
                                                        <img src={arrayData.img} className="card-img-top" alt="..." style={{ height: "120px", objectFit: "fill" }} />
                                                        <div className="card-body">
                                                            <h5 className="card-title">{arrayData.name}</h5>
                                                            <div className='container w-100 p-0'>
                                                                <span className='m-1 d-block'>Quantity: {arrayData.qty}</span>
                                                                <span className='m-1 d-block'>Size: {arrayData.size}</span>
                                                                <span className='m-1 d-block'>Price: ₹{arrayData.price}/-</span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    )) : <div>No Orders Found</div>}
                </div>
            </div>

            <Footer />
        </div>
    )
}
